mapper.select:
SELECT f.ID AS id, f.PID AS pId, f.`NAME` AS name, f.TYPE_CODE AS typeCode, t.`NAME` AS typeName, f.PATH AS path, f.FILESIZE AS fileSize, f.REMARKS as remarks, date_format( f.CREATE_TIME, '%Y-%m-%d %h:%i:%s %p') AS createTime FROM ts_file_info f LEFT JOIN tr_type t ON f.TYPE_CODE = t.`CODE` WHERE f.TYPE_CODE != 'tp_folder'
AND f.PID = #{pId}
AND f.TYPE_CODE = #{typeCode}
AND f.`NAME` like '%${name}%'
AND f.`PATH` like '%${path}%'
ORDER BY f.CREATE_TIME DESC
LIMIT #{offset},#{pageSize}

SELECT count(0) FROM ts_file_info f LEFT JOIN tr_type t ON f.TYPE_CODE = t.`CODE` WHERE f.TYPE_CODE != 'tp_folder'
AND f.PID = #{pId}
AND f.TYPE_CODE = #{typeCode}
AND f.`NAME` like '%${name}%'
AND f.`PATH` like '%${path}%'
